        """ Input shape [batch, feats, seq]"""
        shared_out = self.shared_block(x)
        res_out = self.res_conv(shared_out)
        if not self.skip_out_chan:
            return res_out, res_out
        skip_out = self.skip_conv(shared_out)
        return res_out, skip_out
